package com.revature.q5;

public class MyStringClass {

	String str;
	// creates a method that accepts a string str and an int idx as parameters
	public static String mySubstringMethod (String str, int idx) {
	
		// method returns the substring of str from 0 to idx - 1
		return str.substring(0, idx - 1);
		
	}
	public MyStringClass() {
		super();
		// TODO Auto-generated constructor stub
	}
	public String getStr() {
		return str;
	}
	public void setStr(String str) {
		this.str = str;
	}
	public MyStringClass(String str) {
		super();
		this.str = str;
	}
}
